mapper.update:
update contest set is_private = #{contest.isPrivate}, start_time = #{contest.startTime}, password = #{contest.password},
end_time = #{contest.endTime}, description = #{contest.description}, real_rank = #{contest.realRank}, title = #{contest.title}
where contest_id = #{contest.contestId}

mapper.select:
select * from contest, user where contest_id in (
    select contest_id from user_contest where user_id = #{userId}
) and contest.user_id = user.user_id

select count(*) from contest where contest_id in (
    select contest_id from user_contest where user_id = #{userId}
)

select * from submit_program
where contest_id = #{contestId} and user_id = #{userId}
limit #{cnt}, #{pageSize}

select distinct user_id from answer where contest_id = #{contestId} and user_id in (
    select student_id from class_student where class_id = #{classId}
)
