mapper.select:
select real_id from problem where problem_id in (
    select problem_id from contest_problem


            contest_id = #{contestId}


) and type_id = #{typeId}

select problem_id from problem where problem_id in (
    select problem_id from contest_problem where contest_id = #{contestId}
) and type_id = #{typeId}

select real_id from problem where problem_id in (
select problem_id from contest_problem


        contest_id = #{contestId}


) and type_id = #{typeId} limit #{cnt}, #{pageSize}

mapper.update:
update contest_problem
set total = total + #{a},
correct = correct + #{b}
where contest_id = #{contestId} and problem_id = #{problemId}
